        var roleStored = [];
        var embed = {
            "title": "Automated Captcha System",
            "description": `Please mention a role, role id, or role name to set a captcha channel for it!\n\nIf you want to disable captcha, please just respond with the number 0\n\nIf you want to enable the captcha but give no role, please respond with the number 1\n\nIf you would like to input multiple roles, please respond by id or mention of the role.\nThe roles should be seperated by spaces for ids. Any mention format works.\n\nYou **cannot** mix mentions and ids. Only the mentions would be counted.`,
            "color": 0x00ff00
        }
        if (msg.guild.me.hasPermission("MANAGE_ROLES")) {

            if (msg.member.hasPermission("MANAGE_GUILD")) {
                msg.channel.send({embed: embed}).then(sentMessage => {
                    msg.channel.awaitMessages(m => m.author.id == msg.author.id, {
                            max: 1,
                            time: 120000,
                            errors: ['time']
                        })
                        .then(collected => {
                            var roleInfo = collected.first();
                            collected.first().delete();

                            if (roleInfo.content == 0) {
                                msg.guild.setData("serverCaptchaRole", 0);
                                embed["description"] = `The captcha system has been disabled!`;
                                sentMessage.edit({
                                    embed: embed
                                });
                            } else if (roleInfo.content == 1) {
                                msg.guild.setData("serverCaptchaRole", 1);
                                embed["description"] = `The captcha system has been enabled but will give no roles!`;
                                sentMessage.edit({
                                    embed: embed
                                });
                            } else {
                                if (roleInfo.mentions.roles.first() != null) {
                                    roleInfo.mentions.roles.each(roles => roleStored.push(roles));
                                } else if (!isNaN(roleInfo.content.split(" ")[0])) {
                                    roleInfo.content.split(" ").forEach(roleIDS => {
                                        if(!isNaN(roleIDS)) {
                                            var attemptFetch = msg.guild.roles.cache.get(roleIDS);

                                            if (attemptFetch != null) {
                                                roleStored.push(attemptFetch);
                                            }
                                        }
                                    })
                                } else {
                                    var attemptFetch = msg.guild.roles.cache.find(role => role.name == roleInfo.content);

                                    if (attemptFetch != null) {
                                        roleStored.push(attemptFetch);
                                    }
                                }

                                if (roleStored.length != 0) {
                                    var finalRoles = [];
                                    var finalMentions = "";
                                    var finalMSG = [];

                                    roleStored.forEach(role => {
                                        if (role.position < msg.guild.me.roles.highest.position) {
                                            finalRoles.push(role.id);
                                            finalMentions += `${role}`
                                        } else {
                                            finalMSG.push(role);
                                        }
                                    })


                                    if (finalRoles.length > 0 ) {
                                        msg.guild.setData("serverCaptchaRole", finalRoles);

                                        var extraMSG = "You have provided a role with a higher position than what I have!\n\nRole(s):\n";
                                        if (finalMSG.length != 0) {
                                            finalMSG.forEach(violatingRole=>{
                                                extraMSG += `${violatingRole} `;
                                            })
                                        }

                                        embed["description"] = `The automatic role(s) to give has been set to: ${finalMentions}!\n\n${finalMSG}`;
                                        sentMessage.edit({
                                            embed: embed
                                        });
                                    } else {
                                        errorMSG(`You have provided a role(s) with a higher position than what I have!`, sentMessage);
                                    }
                                } else {
                                    errorMSG(`You did not provide a valid role mention, id, or name!`, sentMessage);
                                }

                            }
                        })
                        .catch(err => {
                            console.log(err);
                            errorMSG(`You did not respond in time!`, sentMessage);
                        })
                })
            } else {

                embed["color"] = 0xff0000;
                embed["description"] = `You do not have the sufficent permission to run this command!`;
                msg.channel.send({
                    embed: embed
                });
            }
        } else {
            embed["color"] = 0xff0000;
            embed["description"] = `I don't have the sufficient permission to give roles!`;
            msg.channel.send({
                embed: embed
            });
        }

        function errorMSG(errorMsg, message) {
            var ErrorEmbed = {
                "title": "Automated Captcha System",
                "description": errorMsg,
                "color": 0xff0000
            }

            message.edit({
                embed: ErrorEmbed
            })
        }

